export default (s) => {
  var i, j, len, pre_is_up, t, up, up_case;
  if (s.length) {
    t = [];
    for (j = 0, len = s.length; j < len; j++) {
      i = s[j];
      if (i === '-') {
        up = true;
        continue;
      }
      if (i === '_') {
        if (!pre_is_up) {
          up = true;
          continue;
        }
      }
      up_case = i.toUpperCase();
      if (up) {
        i = up_case;
        up = void 0;
        pre_is_up = true;
      } else if ('9876543210'.includes(i)) {
        pre_is_up = false;
      } else {
        pre_is_up = i === up_case;
      }
      t.push(i);
    }
    return t.join('');
  } else {
    return s;
  }
};
